(5017, 1, '押运事故不包括（）。', 1, 'peadmin', '', '&lt;p&gt;A: 遗漏箱包&lt;/p&gt;&lt;p&gt;B: 遗漏枪支&lt;/p&gt;&lt;p&gt;C: 遗漏网点&lt;/p&gt;&lt;p&gt;D:  金库拒不发箱&lt;/p&gt;', 4, 'B', '&lt;/p&gt;', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"4";s:5:"knows";s:6:"四级";}}', 1532870570, 1, '', 0, 0, 1, '', 0),
(5018, 1, '押运事故不包括（）。', 1, 'peadmin', '', '&lt;p&gt;A: 遗漏箱包&lt;/p&gt;&lt;p&gt;B: 枪支损坏&lt;/p&gt;&lt;p&gt;C: 遗漏网点&lt;/p&gt;&lt;p&gt;D:  金库拒不发箱&lt;/p&gt;', 4, 'B', '&lt;/p&gt;', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"4";s:5:"knows";s:6:"四级";}}', 1532870570, 1, '', 0, 0, 1, '', 0),
