(6418, 1, '（），应使对象对进行查问的根据感到合乎情理，得到理解，目的是避免发生误会，防止激化和纠纷。', 1, 'peadmin', '', '&lt;p&gt;A:合理原则&lt;/p&gt;&lt;p&gt;B:怀疑的原则&lt;/p&gt;&lt;p&gt;C:距离的原则&lt;/p&gt;&lt;p&gt;D:两防的原则&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6419, 3, '一旦发现可疑目标，选择适当时机对可疑人员进行查问，则可由保安人员自己相机决断。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6420, 3, '没有取得犯罪嫌疑人的证据不能查问。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6421, 1, '（）会造成证据转移或可疑人员逃窜。', 1, 'peadmin', '', '&lt;p&gt;A:查问太早&lt;/p&gt;&lt;p&gt;B:跟踪&lt;/p&gt;&lt;p&gt;C:盯梢&lt;/p&gt;&lt;p&gt;D:查问太晚&lt;/p&gt;', 4, 'D', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6422, 1, '根据现场环境和违法犯罪动向，保安人员经常采用“（）”战术方法，待其露出马脚再出击。', 1, 'peadmin', '', '&lt;p&gt;A:声东击西&lt;/p&gt;&lt;p&gt;B:欲擒故纵&lt;/p&gt;&lt;p&gt;C:敲山震虎&lt;/p&gt;&lt;p&gt;D:引蛇出洞&lt;/p&gt;', 4, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6423, 3, '应尽量选择光线条件较好的地点进行查问。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6424, 3, '保安人员发现可疑情况后，周围环境不宜进行查问的地点，也可进行查问。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6425, 1, '如出现时机恰当，地点不太理想时，应以把握“（）”的原则。', 1, 'peadmin', '', '&lt;p&gt;A:主动出击&lt;/p&gt;&lt;p&gt;B:时机为主，选择地点为辅&lt;/p&gt;&lt;p&gt;C:掌握地点&lt;/p&gt;&lt;p&gt;D:合法&lt;/p&gt;', 4, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6426, 1, '（）是指便于工作，防止其行凶或逃跑，尽量选择在光线条件较好的地点。', 1, 'peadmin', '', '&lt;p&gt;A:宜直不宜弯&lt;/p&gt;&lt;p&gt;B:宜静不宜闹&lt;/p&gt;&lt;p&gt;C:宜宽不宜窄&lt;/p&gt;&lt;p&gt;D:宜明不宜暗&lt;/p&gt;', 4, 'D', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6427, 3, '发现重大犯罪嫌疑人时千万不要急于拦截，应先报告要求支援。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6428, 3, '接近时，当嫌疑人背对保安人员时，保安人员应在其身后右侧约2米的位置站立。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6429, 1, '当犯罪嫌疑人人数较少，年龄较大，携物较多，行动不便，或尚未发现保安人员，毫无逃跑迹象时，可采取（）接近。', 1, 'peadmin', '', '&lt;p&gt;A:正面&lt;/p&gt;&lt;p&gt;B:侧后&lt;/p&gt;&lt;p&gt;C:后面&lt;/p&gt;&lt;p&gt;D:迂回&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6430, 1, '当犯罪嫌疑人人数较多，且年轻力壮，手中可能持有凶器，有逃跑企图时，保安人员可采取正面牵制，（）包围的方法接近。', 1, 'peadmin', '', '&lt;p&gt;A:突然袭击&lt;/p&gt;&lt;p&gt;B:左、右两侧迂回&lt;/p&gt;&lt;p&gt;C:正面接近&lt;/p&gt;&lt;p&gt;D:侧后&lt;/p&gt;', 4, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6431, 3, '保安人员通过识别判断，能够确认犯嫌疑人时，应立即报告，并实施有效的控制。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6432, 3, '保安人员发现被绑架的人求救时，立即报警，隐蔽等待增援。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6433, 1, '保安人员盘查可疑人时经常采用的拦截方式是（）。', 1, 'peadmin', '', '&lt;p&gt;A:跟踪法&lt;/p&gt;&lt;p&gt;B:包抄法&lt;/p&gt;&lt;p&gt;C:迂回法&lt;/p&gt;&lt;p&gt;D:正面法&lt;/p&gt;', 4, 'C', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6434, 1, '保安人员站在几个被拦截对象的（）进行查问最危险。', 1, 'peadmin', '', '&lt;p&gt;A:前面&lt;/p&gt;&lt;p&gt;B:后面&lt;/p&gt;&lt;p&gt;C:侧面&lt;/p&gt;&lt;p&gt;D:中间&lt;/p&gt;', 4, 'D', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6435, 3, '查问一般可以分为四个步骤，即起问、查问、查验、处理。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6436, 3, '起问就是直接了当地开始查问，直奔主题。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6437, 1, '以关心、友好的形式进行攀谈，寻找其言行的疑点是（）起问。', 1, 'peadmin', '', '&lt;p&gt;A:询问&lt;/p&gt;&lt;p&gt;B:一般性询问&lt;/p&gt;&lt;p&gt;C:交谈式&lt;/p&gt;&lt;p&gt;D:盘查&lt;/p&gt;', 4, 'C', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6438, 1, '发现具有实质性疑点时，即可进入（）阶段。', 1, 'peadmin', '', '&lt;p&gt;A:截停&lt;/p&gt;&lt;p&gt;B:查问&lt;/p&gt;&lt;p&gt;C:盘问&lt;/p&gt;&lt;p&gt;D:缉捕&lt;/p&gt;', 4, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6439, 3, '查问的方法之一是提出问题引导对方自述。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6440, 3, '查问中发现对象回答前后矛盾、内容夹杂谎言、回答含糊其辞、有意答非所问、自己无法解释或闭口不予回答的情况，要立即报警，并带到保安室进行审查。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0);
INSERT INTO `x2_questions` (`questionid`, `questiontype`, `question`, `questionuserid`, `questionusername`, `questionlastmodifyuser`, `questionselect`, `questionselectnumber`, `questionanswer`, `questiondescribe`, `questionknowsid`, `questioncreatetime`, `questionstatus`, `questionhtml`, `questionparent`, `questionsequence`, `questionlevel`, `questiondeler`, `questiondeltime`) VALUES
(6441, 1, '保安人员查问的“十看、十对”要诀中，以下（）的表述是错的。', 1, 'peadmin', '', '&lt;p&gt;A:看证件对姓名&lt;/p&gt;&lt;p&gt;B:看原籍对口音&lt;/p&gt;&lt;p&gt;C:看面貌对关系&lt;/p&gt;&lt;p&gt;D:看物品对来由&lt;/p&gt;', 4, 'C', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6442, 1, '现场若反复查问不能查清疑点或越问疑点越多的情况，保安人员应果断（）。', 1, 'peadmin', '', '&lt;p&gt;A:带离&lt;/p&gt;&lt;p&gt;B:控制&lt;/p&gt;&lt;p&gt;C:搜身&lt;/p&gt;&lt;p&gt;D:报警&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6443, 3, '证件查验的目的就是判定证件本身的真伪、证件内容的真伪。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6444, 3, '保安人员在查验居民身份证、工作证、介绍信时，要特别注意涂改的证件。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6445, 1, '人与证对照主要是对照证件照片形象与持证人是否相像或同一，或（）。', 1, 'peadmin', '', '&lt;p&gt;A:证件内所载内容与持证人陈述是否一致&lt;/p&gt;&lt;p&gt;B:证件规格与图像&lt;/p&gt;&lt;p&gt;C:观察特殊印记与暗记&lt;/p&gt;&lt;p&gt;D:证件质地&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6446, 1, '他人的证件被持有者占为已有并加以使用的是（）。', 1, 'peadmin', '', '&lt;p&gt;A:冒用的证件&lt;/p&gt;&lt;p&gt;B:伪造的证件&lt;/p&gt;&lt;p&gt;C:假证件&lt;/p&gt;&lt;p&gt;D:涂改的证件&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6447, 3, '有可能是犯罪工具和犯罪物证之物，保安人员均应收缴和搜身。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6448, 3, '对于非违法犯罪之物品，查验后可暂时保管。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6449, 1, '保安人员对嫌疑车辆进行查验时，应令驾驶员（），车上其他人员下车集中后进行查验。', 1, 'peadmin', '', '&lt;p&gt;A:熄火、下车&lt;/p&gt;&lt;p&gt;B:双手包头&lt;/p&gt;&lt;p&gt;C:下车搜身&lt;/p&gt;&lt;p&gt;D:在驾驶室&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6450, 1, '保安人员查验物品所用的（），是以交流磁场的微小变化为根据，当遇有外界磁场干扰时，线圈发生感应，从而引起报警。', 1, 'peadmin', '', '&lt;p&gt;A:手持金属探测器&lt;/p&gt;&lt;p&gt;B:炸弹检测器&lt;/p&gt;&lt;p&gt;C:水下专用探测器&lt;/p&gt;&lt;p&gt;D:X射线&lt;/p&gt;', 4, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6451, 3, '保安人员发现车上载有可疑物品、违禁物品或走私物品等，就要及时对车辆进行搜查。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'B', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6452, 3, '机动车辆作案或肇事车辆逃逸，车身由于碰撞、擦蹭会留下痕迹。（）', 1, 'peadmin', '', '&lt;p&gt;A:正确&lt;/p&gt;&lt;p&gt;B:错误&lt;/p&gt;', 2, 'A', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6453, 1, '当嫌疑车辆被拦截以后，必要时应收存其（）以防逃跑。', 1, 'peadmin', '', '&lt;p&gt;A:身份证&lt;/p&gt;&lt;p&gt;B:驾驶证&lt;/p&gt;&lt;p&gt;C:车钥匙&lt;/p&gt;&lt;p&gt;D:行驶证&lt;/p&gt;', 4, 'C', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
(6454, 1, '保安人员在车上查可疑物，一般是采用翻动、抽查的方法，必要时也可采用（）的方法检查。', 1, 'peadmin', '', '&lt;p&gt;A:全面检查&lt;/p&gt;&lt;p&gt;B:扣车带离&lt;/p&gt;&lt;p&gt;C:网上排查&lt;/p&gt;&lt;p&gt;D:全部卸车&lt;/p&gt;', 4, 'D', '', 'a:1:{i:0;a:2:{s:7:"knowsid";s:1:"5";s:5:"knows";s:6:"五级";}}', 1532870603, 1, '', 0, 0, 1, '', 0),
